import React, {useEffect, useState} from 'react'
import {Link} from 'react-router-dom'
import '../css/menuindex.css';

const DtlCodeIndex = () => {
    const params = new URLSearchParams(window.location.search);
    let start = params.get("start") == null ? 0 : params.get("start");
    let paramSearchKey = params.get("searchKey") == null ? "mst_cd" : params.get("searchKey");
    let paramKeyword = params.get("keyword") == null ? "" : params.get("keyword");
    let paramCode = params.get("code");

    if(paramCode !== "" || paramCode !== null) {
        alert("paramCode : "  + paramCode);
        alert("잘못된 접근 입니다.");
        window.location.href = "/commonCode/MstCodeIndex";
    }

    let idx = 1 + parseInt(start);
    
    const [dtls,setDtls] = useState([]);
    const [total, setTotal] = useState(0);
    const [searchKey, setSearchKey] = useState(paramSearchKey);
    const [keyword, setKeyword] = useState(paramKeyword);

    const scale = 2;
    const pageScale = 5;
    const currentPage = Math.ceil(start / pageScale) + 1;
    const pageNumbers = [];
    const totalPage = Math.ceil(total / scale); // 전체 페이징 수
    const totalPageScale = Math.ceil(totalPage/pageScale); //  총 페이지 수
    const pageNum = Math.ceil(currentPage/pageScale); // 페이지 번호
    const startPage = pageScale * (pageNum - 1) + 1; // 페이지 시작 번호
    const nextPage = pageNum * pageScale + 1; // 다음 페이지 시작 번호
    const prevPage = nextPage - pageScale - 1; // 이전 페이지 
    
    for (let num = startPage; num<= startPage+pageScale-1; num++) {
        if(num <= totalPage){
            pageNumbers.push(num);
        }
    }

    let formData = new FormData();
    formData.append('start', start);
    formData.append('scale', scale);
    formData.append('searchKey', searchKey);
    formData.append('keyword', keyword);

    let deleteFormData = new FormData();

    useEffect(() => {
        fetch('http://localhost:9191/api/code/dtlIndex', {
            method:"POST",
            body : formData
        })
        .then((res) => res.json())
        .then((res)=>{
            console.log(res);
            setDtls(res.dtlList);
            setTotal(res.total);
        });
    }, []);

    const handleSubmit = (e, dtlCd) => {
        if(window.confirm("삭제 하시겠습니까?")) {
            e.preventDefault();
            fetch('http://localhost:9191/api/code/delete', {
                method: "POST",
                headers: {
                    "Content-Type": "application/json; charset=utf-8"
                },
                body: dtlCd
            })
            .then((response) => {
                if(response.status == 200) {
                    alert("삭제 되었습니다.");
                    window.location.reload();
                }
            });
        }
    };

    const changeKeywordValue = (e) => {
        setKeyword(e.target.value);
    }

    const changeSearchKeyValue = (e) => {
        setSearchKey(e.target.value);
    }
    
    return (
        <div class="content">
            <h2>공통 코드 관리</h2>
            <div class="search-container">
                <select class="search-select" name="searchKey" onChange={(e) => changeSearchKeyValue(e)}>
                    <option value="mst_cd" selected={searchKey == "mst_cd"}>코드</option>
                    <option value="mst_nm" selected={searchKey == "mst_nm"}>코드명</option>
                </select>
                <input type="text" class="search-input" name="keyword" value={keyword} onChange={(e) => changeKeywordValue(e)} />
                <a class="search-button" href={`/commonCode/DtlCodeIndex?searchKey=${searchKey}&keyword=${keyword}`}>검색</a>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>번호</th>
                        <th>마스터 코드</th>
                        <th>상세 코드</th>
                        <th>코드명</th>
                        <th>내용</th>
                        <th>관리</th>
                    </tr>
                </thead>
                <tbody>
                    {dtls.map((dtl)=>(
                        <tr>
                            <td>{idx++}</td>
                            <td>{dtl.mstCd}</td>
                            <td>{dtl.dtlCd}</td>
                            <td>{dtl.dtlNm}</td>
                            <td>{dtl.dsctn}</td>
                            <td>
                                <button type="button"><Link to={`/commonCode/CodeReg?code=${dtl.dtlCd}&type=dtl`}>수정</Link></button>
                                <button type="button" onClick={(e) => handleSubmit(e, dtl.dtlCd)}>삭제</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button type="button"><Link to="/commonCode/CodeReg">등록</Link></button>
           
            <div class="pagination">
                <a href={pageNum > 1 ? `/commonCode/DtlCodeIndex?start=${(prevPage - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`: '#'}>&laquo;</a>
                {pageNumbers.map(number =>(
                    <a href={`/commonCode/DtlCodeIndex?start=${(number - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`} class={currentPage === number ? 'active' : ''} key={number}>{number}</a>
                ))}
                <a href={pageNum < totalPageScale ? `/commonCode/DtlCodeIndex?start=${(nextPage - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`: '#'}>&raquo;</a>
            </div>
            
        </div>
    );
};

export default DtlCodeIndex;
