import React, { useEffect, useState, useContext } from "react";
import { useNavigate, useLocation } from "react-router-dom"; // ‚úÖ Î¶¨ÎîîÎ†âÌä∏ Í∏∞Îä• Ï∂îÍ∞Ä
import { MemberInfoContext } from "../components/MemberInfoContext"; // ‚úÖ Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
import "../aiRequest/css/reqStyle.css";

const SrvyRslt = () => {
  const navigate = useNavigate();
  const memberInfo = useContext(MemberInfoContext); // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  const location = useLocation();

  // URLÏóêÏÑú groupNo Í∞ÄÏ†∏Ïò§Í∏∞ (Ïù¥Î©îÏùºÏùÄ Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ÏóêÏÑú Í∞ÄÏ†∏Ïò¥)
  const params = new URLSearchParams(location.search);
  const groupNo = params.get("groupNo") || "";
  const email = params.get("email") || "";
  const [surveyResults, setSurveyResults] = useState([]);

  useEffect(() => {
    fetch(
      `http://localhost:9193/api/SrvyRslt?groupNo=${groupNo}&memId=${email}`,
      {
        method: "GET",
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log("üìä Í∞úÏù∏ ÎßåÏ°±ÎèÑ Ï°∞ÏÇ¨ ÏùëÎãµ API ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", data);

        if (data.length > 0) {
          setSurveyResults(data);
        } else {
          console.warn("‚ö†Ô∏è ÎßåÏ°±ÎèÑ Ï°∞ÏÇ¨ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.");
        }
      })
      .catch((error) => {
        console.error("‚ùå Error fetching survey results:", error);
      });
  }, [groupNo, memberInfo, navigate]); // ‚úÖ ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÏóÖÎç∞Ïù¥Ìä∏

  return (
    <div className="aiR-container">
      <h1>üìä Í∞úÏù∏ ÎßåÏ°±ÎèÑ Ï°∞ÏÇ¨ Í≤∞Í≥º</h1>
      {surveyResults.length === 0 ? (
        <p>‚ö†Ô∏è ÎßåÏ°±ÎèÑ Ï°∞ÏÇ¨ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <form>
          {surveyResults.map((result, index) => (
            <div className="question" key={index}>
              <label>{result.srvyCn}</label>

              {/* Í∞ùÍ¥ÄÏãù ÏùëÎãµ ÌëúÏãú */}
              {result.chcRslt && (
                <div className="satisfaction-options">
                  <div>
                    <input
                      type="radio"
                      id={`satisfaction-${index}`}
                      name={`satisfaction-${index}`}
                      value={result.chcRslt || ""}
                      disabled
                      checked
                    />
                    <label htmlFor={`satisfaction-${index}`}>
                      {result.chcRslt}
                    </label>
                  </div>
                </div>
              )}

              {/* Îã®ÎãµÌòï ÏùëÎãµ ÌëúÏãú */}
              {result.textRslt && (
                <div className="text-responses">
                  <h4>Îã®ÎãµÌòï ÏùëÎãµ:</h4>
                  <p>üìù {result.textRslt}</p>
                </div>
              )}
            </div>
          ))}
          <div className="submit-section">
            <button type="button" onClick={() => window.history.back()}>
              Îí§Î°úÍ∞ÄÍ∏∞
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default SrvyRslt;
