import React, {useEffect, useState} from 'react'
import {Link} from 'react-router-dom'
import '../css/menuindex.css';

const MenuIndex = () => {
    const[msts,setMsts] = useState([]);
    let i = 1;

    const params = new URLSearchParams(window.location.search);
    let start = params.get("start") == null ? 0 : params.get("start");
    let paramSearchKey = params.get("searchKey") == null ? "mst_cd" : params.get("searchKey");
    let paramKeyword = params.get("keyword") == null ? "" : params.get("keyword");
    
    const [total, setTotal] = useState(0);
    const [searchKey, setSearchKey] = useState(paramSearchKey);
    const [keyword, setKeyword] = useState(paramKeyword);

    const scale = 5;
    const pageScale = 5;
    const currentPage = Math.ceil(start / pageScale) + 1;
    const pageNumbers = [];
    const totalPage = Math.ceil(total / scale); // 전체 페이징 수
    const totalPageScale = Math.ceil(totalPage/pageScale); //  총 페이지 수
    const pageNum = Math.ceil(currentPage/pageScale); // 페이지 번호
    const startPage = pageScale * (pageNum - 1) + 1; // 페이지 시작 번호
    const nextPage = pageNum * pageScale + 1; // 다음 페이지 시작 번호
    const prevPage = nextPage - pageScale - 1; // 이전 페이지 
    
    for (let num = startPage; num<= startPage+pageScale-1; num++) {
        if(num <= totalPage){
            pageNumbers.push(num);
        }
    }

    var formData = new FormData();
    formData.append('start', start);
    formData.append('searchKey', searchKey);
    formData.append('keyword', keyword);

    useEffect(() => {
        fetch('http://localhost:9191/api/code/mstIndex', {
            method:"POST",
            heaers: {
                "Content-Type": "charset=utf-8"
            },
            body : formData
        })
        .then((res) => res.json())
        .then((res)=>{
            console.log(res);
            setMsts(res.mstList);
            setTotal(res.total);
        });
    }, []);

    const handleSubmit = (e, mstCd) => {
        if(window.confirm("삭제 하시겠습니까?")) {
            e.preventDefault();
            fetch('http://localhost:9191/api/code/delete', {
                method: "POST",
                headers: {
                    "Content-Type": "application/json; charset=utf-8"
                },
                body: mstCd
            })
            .then((response) => {
                if(response.status == 200) {
                    alert("삭제 되었습니다.");
                    window.location.reload();
                }
            });
        }
    };

    const changeKeywordValue = (e) => {
        setKeyword(e.target.value);
    }

    const changeSearchKeyValue = (e) => {
        setSearchKey(e.target.value);
    }
    
    return (
        <div class="content">
            <h2>메뉴관리</h2>
            <div class="search-container">
                <select class="search-select" name="searchKey" onChange={(e) => changeSearchKeyValue(e)}>
                    <option value="title" selected={searchKey == "title"}>제목</option>
                    <option value="user" selected={searchKey == "user"}>작성자</option>
                </select>
                <input type="text" class="search-input" name="keyword" value={keyword} onChange={(e) => changeKeywordValue(e)} />
                <a class="search-button" href={`/ntc/NtcIndex?searchKey=${searchKey}&keyword=${keyword}`}>검색</a>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>번호</th>
                        <th>코드</th>
                        <th>코드명</th>
                        <th>상세코드 조회</th>
                        <th>관리</th>
                    </tr>
                </thead>
                <tbody>
                    {msts.map((mst)=>(
                        <tr>
                            <td>{i}</td>
                            <td>{mst.mstCd}</td>
                            <td>{mst.mstNm}</td>
                            <td><button type="button"><Link to={`/commonCode/CodeIndex?code=${mst.mstCd}`}>상세코드 조회</Link></button></td>
                            <td>
                                <button type="button"><Link to={`/commonCode/CodeReg?code=${mst.mstCd}&type=mst}`}>수정</Link></button>
                                <button type="button" onClick={(e) => handleSubmit(e, mst.mstCd)}>삭제</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button type="button"><Link to="/commonCode/CodeReg">등록</Link></button>
           
            <div class="pagination">
                <a href={pageNum > 1 ? `/commonCode/CodeIndex?start=${(prevPage - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`: '#'}>&laquo;</a>
                {pageNumbers.map(number =>(
                    <a href={`/commonCode/CodeIndex?start=${(number - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`} class={currentPage === number ? 'active' : ''} key={number}>{number}</a>
                ))}
                <a href={pageNum < totalPageScale ? `/commonCode/CodeIndex?start=${(nextPage - 1) * scale}&searchKey=${searchKey}&keyword=${keyword}`: '#'}>&raquo;</a>
            </div>
            
        </div>
    );
};

export default MenuIndex;
