import React, {useState,useEffect} from 'react'
import {Link,useParams } from 'react-router-dom'
import '../css/menureg.css';

const MbrJoin = () => {
     const params = new URLSearchParams(window.location.search);
    let paramNo = params.get("no");

    const[menureg,setMenureg] = useState({
        email: '',
        phone: '',
        zip: '',
        address1: '',
        address2: ''

        });

    useEffect(() => {
         // 다음 주소 API 스크립트 로드
        const script = document.createElement("script");
        script.src = "//t1.daumcdn.net/mapjsapi/bundle/postcode/prod/postcode.v2.js";
        script.async = true;
        document.body.appendChild(script);

        return () => {
        document.body.removeChild(script);
        };

        // fetch('http://localhost:9191/api/getmenu',{
        //     method:"POST",
        //     headers:{
        //         "Content-Type":"application/json; charset=utf-8"
        //     },
        //     body: JSON.stringify({no:paramNo})
        // })
        // .then((response)=>{
        //     if(response.status==200){
        //         if(paramNo){
        //             response.json().then(data => {
        //                 setMenureg(prevState => ({
        //                     ...prevState,
        //                     no: data.no,
        //                     rfrncNo: data.rfrncNo,
        //                     rfrncGroup: data.rfrncGroup,
        //                     menuNm: data.menuNm,
        //                     menuLnkg: data.menuLnkg,
        //                     rlsMbrAuthrt: data.rlsMbrAuthrt,
        //                     mngrPageRlsYn: data.mngrPageRlsYn,
        //                     userPageRlsYn: data.userPageRlsYn,
        //                     useYn: data.useYn,
        //                     regId: data.regId,
        //                     uptId: data.uptId,
        //                     regDt: data.regDt,
        //                     uptDt: data.uptDt,
        //                     layer: data.layer
        //                 }));
        //             })
        //         }
        //     }
        // });
    },[]);
     
    
        const changeValue=(e)=>{
            setMenureg({
                ...menureg,
                [e.target.name]:e.target.value
            })
        }

        const changeChkValue=(e)=>{
            const { name, checked } = e.target;
            setMenureg((menureg)=>({
                ...menureg,
                [e.target.name]: checked ? "Y":"",
            }))
        }

        
        
       
        const handleSubmit = (e) => {
            e.preventDefault();
            let apiPath="";
            // if(paramNo){
            //     apiPath = "http://localhost:9191/api/menuupdate";
            // }else{
            //     apiPath = "http://localhost:9191/api/menuform";
            // }
           
            // fetch(apiPath,{
            //     method:"POST",
            //     headers:{
            //         "Content-Type":"application/json; charset=utf-8"
            //     },
            //     body: JSON.stringify(menureg)
            // })
            // .then((response)=>{
            //     if(response.status==200){
            //         window.location.href = "./MenuIndex";
            //         if(paramNo){
            //             alert("수정되었습니다.");
            //         }else{
            //             alert("등록되었습니다.");
            //         }
            //     }
            // });
        };

         // 주소 검색 실행 함수
        const handlePostcode = () => {
            new window.daum.Postcode({
            oncomplete: (data) => {
                let addr = ""; // 도로명 주소
                let extraRoadAddr = ""; // 추가 정보

                if (data.userSelectedType === 'R') { // 사용자가 도로명 주소를 선택했을 경우
                    addr = data.roadAddress;
                } else { // 사용자가 지번 주소를 선택했을 경우(J)
                    addr = data.jibunAddress;
                }

                if(data.userSelectedType === 'R'){
                    if (data.bname !== "" && /[동|로|가]$/g.test(data.bname)) {
                    extraRoadAddr += data.bname;
                    }
                    if (data.buildingName !== "" && data.apartment === "Y") {
                    extraRoadAddr += extraRoadAddr !== "" ? `, ${data.buildingName}` : data.buildingName;
                    }
                    if (extraRoadAddr !== "") {
                    extraRoadAddr = ` (${extraRoadAddr})`;
                    }
                }

                setMenureg((prev) => ({
                    ...prev,
                    address1: addr,
                    zip: data.zonecode,
                    
                  }));
            },
            }).open();
        };

         
        

    return (
        <div>
            <div id="content_center" style={{ textAlign: 'center', width: '1800px' }}>
                <h1 class="title" >회원가입</h1>
            </div>
            <form onSubmit={handleSubmit}>
                <div class="form-container">
                    <div class="menu-form-group">
                        <label for="menu-name">이메일</label>
                        <input type="email" id="menu-name" name="email" value={menureg.menuNm} onChange={(e) => changeValue(e)} placeholder="이메일을 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="link">비밀번호</label>
                        <input type="password" id="link" name="password" value={menureg.menuLnkg} onChange={(e) => changeValue(e)} placeholder="비밀번호를 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="menu-name">주소</label>
                        <button type="button" onClick={handlePostcode}>
                            주소 검색
                        </button>
                        <input type="email" id="menu-name" name="zip" value={menureg.zip} onChange={(e) => changeValue(e)} placeholder="이메일을 입력하세요" />
                        <input type="email" id="menu-name" name="address1" value={menureg.address1} onChange={(e) => changeValue(e)} placeholder="이메일을 입력하세요" />
                        <input type="email" id="menu-name" name="address2" value="" onChange={(e) => changeValue(e)} placeholder="이메일을 입력하세요" />
                    </div>
                    {/* <div class="menu-form-group">
                        <label>사용 여부</label>
                        <div class="checkbox-group">
                            <input type="checkbox" name="useYn" id="use" value="Y" checked={menureg.useYn == 'Y'} onChange={changeChkValue}/><label for="use"> 사용</label>
                        </div>
                    </div> */}
                    <div class="menu-button-group">
                        <button type="button"><Link to="/menu/MenuIndex">목록</Link></button>
                        <button type="submit">등록(수정)</button>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default MbrJoin;
