import React, {useState,useEffect} from 'react'
import {Link,useParams } from 'react-router-dom'
import '../css/menureg.css';

const CodeReg = () => {
    const params = new URLSearchParams(window.location.search);
    let paramCode = params.get("code");
    let paramType = params.get("type");

    const[codeReg,setCodeReg] = useState({
        dtlCd : '',
        dtlNm : '',
        desc : '',
        mstCd : ''
    });
    const[mstCodeReg,setMstCodeReg] = useState([]);
    const[codeOption, setCodeOption] = useState('mst');
    const[mstValue, setMstValue] = useState('');

    let start = 0;
    let scale = 5;

    useEffect(() => {
        const mstCodeFetch = async () => {
            try {
                const response = await fetch('http://localhost:9191/api/code/getMstCode');
                const data = await response.json();
                setMstCodeReg(data);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
      
        mstCodeFetch();

        if(paramCode) {
            const codeFetch = async () => {
                try {
                    const response = await fetch(`http://localhost:9191/api/code/getCode`, {
                        method:"POST",
                        headers:{
                            "Content-Type":"application/x-www-form-urlencoded; charset=utf-8"
                        },
                        body: params
                    });
                    const data = await response.json();
                    setCodeReg(data);
                    setCodeOption(data.type);
                } catch (error) {
                    console.error("Error fetching data:", error);
                }
            };
            codeFetch();
        }
    }, []);
     
    
    const changeValue=(e)=>{
        setCodeReg({
            ...codeReg,
            [e.target.name]:e.target.value
        })
    }

    const changeTypeValue=(e)=>{
        setCodeReg({
            ...codeReg,
            [e.target.name]:e.target.value
        })
        setCodeOption(e.target.value);
        setMstValue('');
    }

    const changeMstCdValue=(e)=>{
        setCodeReg({
            ...codeReg,
            [e.target.name]:e.target.value
        })
        setMstValue(e.target.value);
    }
    
    const handleSubmit = (e) => {
        e.preventDefault();

        if(codeOption === "dtl" && mstValue === "") {
            alert("마스터 코드를 선택해주세요.");
            return false;
        }
        if(codeReg.dtlCd === "") {
            alert("코드를 입력해주세요.");
            return false;
        }
        if(codeReg.dtlNm === "") {
            alert("코드명을 입력해주세요.");
            return false;
        }
        let apiPath="";

        if(paramCode && paramType){
            apiPath = "http://localhost:9191/api/code/modify";
        }else{
            apiPath = "http://localhost:9191/api/code/reg";
        }
        
        fetch(apiPath,{
            method:"POST",
            headers:{
                "Content-Type":"application/json; charset=utf-8"
            },
            body: JSON.stringify({
                dtlVO : codeReg,
                type : codeOption
            })
        })
        .then((response)=>{
            if(response.status==200){
                const data = response.json();
                alert(data);
                //window.location.href = "/commonCode/CodeIndex";
            }
        });
    };

    return (
        <div>
            <div id="content_center" style={{ textAlign: 'center', width: '1800px' }}>
                <h1 class="title" >공통 코드 {paramCode !== null ? '수정' : '등록'}</h1>
            </div>
            <form onSubmit={handleSubmit}>
                <div class="form-container">
                    <div class="menu-form-group">
                        <label>코드 선택</label>
                        <div class="radio-group">
                            <input type="radio" id="type1" name="type" value="mst" checked={codeOption === "mst"} onChange={changeTypeValue}/><label for="type1">마스터 코드</label>
                            <input type="radio" id="type2" name="type" value="dtl" checked={codeOption === "dtl"} onChange={changeTypeValue}/> <label for="type2">상세 코드</label>
                        </div>
                    </div>
                    <div class="menu-form-group" style={{ display: codeOption === "dtl" ? 'flex' : 'none' }}>
                        <label for="menu-name">마스터 코드</label>
                        <select name="mstCd" value={mstValue} onChange={changeMstCdValue}>
                            <option value="">선택하세요.</option>
                            {mstCodeReg.map((item)=>(
                                <option key={item.mstCd} value={item.mstCd}>{item.mstNm}</option>
                            ))}
                        </select>
                    </div>
                    <div class="menu-form-group">
                        <label for="menu-name">코드</label>
                        <input type="text" id="menu-name" name="dtlCd" value={codeReg.dtlCd} onChange={(e) => changeValue(e)} placeholder="코드를 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="mstNm">코드명</label>
                        <input type="text" id="name" name="dtlNm" value={codeReg.dtlNm} onChange={(e) => changeValue(e)} placeholder="코드명을 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="link">내용</label>
                        <textarea placeholder="내용을 입력하세요." name="desc" rows="4" onChange={(e) => changeValue(e)} value={codeReg.desc}></textarea>
                    </div>
                    <div class="menu-button-group">
                        <button type="button"><Link to="/menu/MenuIndex">목록</Link></button>
                        {/* <button type="button" onClick={window.location.href='/'}>목록</button> */}
                        <button type="submit">{paramCode != null ? '수정' : '등록'}</button>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default CodeReg;
