import React, {useState,useEffect} from 'react'
import {Link,useParams } from 'react-router-dom'
import '../css/menureg.css';

const CodeReg = () => {
    const params = new URLSearchParams(window.location.search);
    let paramCode = params.get("code");

    const[codeReg,setCodeReg] = useState([]);
    const[mstCodeReg,setMstCodeReg] = useState([]);

    let start = 0;
    let scale = 5;

    useEffect(() => {
        const mstCodeFetch = async () => {
            try {
                const response = await fetch('http://localhost:9191/api/code/getMstCode');
                const data = await response.json();
                setMstCodeReg({
                    ...mstCodeReg,
                    mstCd: data.mstCd,
                    mstNm: data.mstNm,
                    desc: data.desc
                });
                console.log(data);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
      
        mstCodeFetch();

        if(paramCode) {
            const codeFetch = async () => {
                try {
                    const response = await fetch('http://localhost:9191/api/code/getCode');
                    const data = await response.json();
                    setCodeReg({
                        ...codeReg,
                        code: data.mstCd,
                        name: data.mstNm,
                        desc: data.desc,
                        type: data.type
                    });
                } catch (error) {
                    console.error("Error fetching data:", error);
                }
            };
            codeFetch();
        }
    }, []);
     
    
    const changeValue=(e)=>{
        setCodeReg({
            ...codeReg,
            [e.target.name]:e.target.value
        })
    }       
    
    const handleSubmit = (e) => {
        e.preventDefault();
        let apiPath="";
        if(paramCode){
            apiPath = "http://localhost:9191/api/code/modify";
        }else{
            apiPath = "http://localhost:9191/api/code/reg";
        }
        
        fetch(apiPath,{
            method:"POST",
            headers:{
                "Content-Type":"application/json; charset=utf-8"
            },
            body: JSON.stringify(codeReg)
        })
        .then((response)=>{
            if(response.status==200){
                window.location.href = "/commonCode/CodeIndex";
                if(paramCode){
                    alert("수정 되었습니다.");
                }else{
                    alert("등록 되었습니다.");
                }
            }
        });
    };

    return (
        <div>
            <div id="content_center" style={{ textAlign: 'center', width: '1800px' }}>
                <h1 class="title" >공통 코드 {paramCode != null ? '수정' : '등록'}</h1>
            </div>
            <form onSubmit={handleSubmit}>
                <div class="form-container">
                    <div class="menu-form-group">
                        <label>코드 선택</label>
                        <div class="radio-group">
                            <input type="radio" id="type1" name="type" value="mst" checked={codeReg.type == "mst" || paramCode == null} onChange={changeValue}/><label for="type1">마스터 코드</label>
                            <input type="radio" id="type2" name="type" value="dtl" checked={codeReg.type == "dtl"} onChange={changeValue}/> <label for="type2">상세 코드</label>
                        </div>
                    </div>
                    <div class="menu-form-group" id="mstCdForm">
                        <label for="menu-name">마스터 코드</label>
                        <select>
                            <option value="">선택하세요.</option>
                            {mstCodeReg.map((item)=>(
                                <option key={start} value={item.mstCd}>{item.mstNm}</option>
                            ))}
                        </select>
                    </div>
                    <div class="menu-form-group">
                        <label for="menu-name">코드</label>
                        <input type="text" id="menu-name" name="code" value={codeReg.code} onChange={(e) => changeValue(e)} placeholder="코드를 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="mstNm">코드명</label>
                        <input type="text" id="name" name="name" value={codeReg.name} onChange={(e) => changeValue(e)} placeholder="코드명을 입력하세요" />
                    </div>
                    <div class="menu-form-group">
                        <label for="link">내용</label>
                        <textarea placeholder="내용을 입력하세요." name="desc" rows="4" onChange={(e) => changeValue(e)} value={codeReg.desc}></textarea>
                    </div>
                    <div class="menu-button-group">
                        <button type="button"><Link to="/menu/MenuIndex">목록</Link></button>
                        {/* <button type="button" onClick={window.location.href='/'}>목록</button> */}
                        <button type="submit">{paramCode != null ? '수정' : '등록'}</button>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default CodeReg;
